const Projects = (props: Props) => (
  <div className="projects-container">
    {probs.projects.map((proj) => (
      <ProjectCard
        title={props.projects.title}
        description={props.projects.description}
        skills={props.projects.skills}
        links={props.projects.links}
        image={props.projects.image}
        key={props.projects.title}
      />
    ))}
  </div>
);

export default Projects;
