import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./_Projects.scss";

class ProjectSkill extends React.PureComponent {
  render() {
    return <span className="project-skill">{this.props.skill}</span>;
  }
}

class ProjectSkills extends React.PureComponent {
  render() {
    return (
      <div className="project-skills">
        {this.props.skills.map((skill, index) => {
          return <ProjectSkill skill={skill} key={index} />;
        })}
      </div>
    );
  }
}

type ProjectsType = {
  title: string;
  description: string;
  skills: string;
  image: string;
  links: string;
  github: string;
};

const ProjectLink = ({ link }: { message: string }) => (
  <div className="project-links">
    {/* <a
          href={preview}
          rel="noopener"
          target="_blank"
          className="project-link"
        >
          Live Demo <FontAwesomeIcon icon="external-link-alt" />
        </a> */}
    <a
      href={github}
      // rel="noopener"
      target="_blank"
      rel="noreferrer"
      className="project-link secondary"
    >
      <FontAwesomeIcon icon={["fab", "github"]} /> View Source
    </a>
  </div>
);

class ProjectLinks extends React.PureComponent {
  render() {
    // const { github, preview } = this.props.links;
    const { github } = this.props.links;
    return (
      <div className="project-links">
        {/* <a
          href={preview}
          rel="noopener"
          target="_blank"
          className="project-link"
        >
          Live Demo <FontAwesomeIcon icon="external-link-alt" />
        </a> */}
        <a
          href={github}
          // rel="noopener"
          target="_blank"
          rel="noreferrer"
          className="project-link secondary"
        >
          <FontAwesomeIcon icon={["fab", "github"]} /> View Source
        </a>
      </div>
    );
  }
}

class ProjectCard extends React.PureComponent<ProjectsType> {
  render() {
    const { image, title, description, skills, links } = this.props;

    return (
      <div className="project-card">
        <img className="project-image" src={image} alt="" />
        <div className="project-content">
          <h3>{title}</h3>
          <p dangerouslySetInnerHTML={{ __html: description }} />
          <ProjectSkills skills={skills} />
          <ProjectLinks links={links} />
        </div>
      </div>
    );
  }
}

type Props = {
  projects: ProjectsType;
};

class Projects extends React.PureComponent<Props> {
  render() {
    const { projects } = this.props;
    return (
      <div className="projects-container">
        <ProjectCard
          title={projects.title}
          description={projects.description}
          skills={projects.skills}
          links={projects.links}
          image={projects.image}
          key={projects.title}
        />
      </div>
    );
  }
}

export default Projects;
