import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./_Projects.scss";

const ProjectSkill = ({ skill }: { skill: string }) => (
  <span className="project-skill">{skill}</span>
);

const ProjectSkills = ({ skills }: { skills: string[] }) => (
  <div className="project-skills">
    {skills.map((skill, index) => {
      return <ProjectSkill skill={skill} key={index} />;
    })}
  </div>
);

type ProjectsType = {
  title: string;
  description: string;
  skills: string[];
  image: string;
  links: Record<"github", string>;
};

const ProjectLinks = ({ links }: { links: Record<"github", string> }) => (
  <div className="project-links">
    {/* <a
          href={preview}
          rel="noopener"
          target="_blank"
          className="project-link"
        >
          Live Demo <FontAwesomeIcon icon="external-link-alt" />
        </a> */}
    <a
      href={links.github}
      // rel="noopener"
      target="_blank"
      rel="noreferrer"
      className="project-link secondary"
    >
      <FontAwesomeIcon icon={["fab", "github"]} /> View Source
    </a>
  </div>
);

class ProjectCard extends React.PureComponent<ProjectsType> {
  render() {
    const { image, title, description, skills, links } = this.props;

    return (
      <div className="project-card">
        <img className="project-image" src={image} alt="" />
        <div className="project-content">
          <h3>{title}</h3>
          <p dangerouslySetInnerHTML={{ __html: description }} />
          <ProjectSkills skills={skills} />
          <ProjectLinks links={links} />
        </div>
      </div>
    );
  }
}

type Props = {
  projects: ProjectsType;
};

class Projects extends React.PureComponent<Props> {
  render() {
    const { projects } = this.props;
    return (
      <div className="projects-container">
        <ProjectCard
          title={projects.title}
          description={projects.description}
          skills={projects.skills}
          links={projects.links}
          image={projects.image}
          key={projects.title}
        />
      </div>
    );
  }
}

export default Projects;
